

formdata = new FormData();

document.getElementById("entrarPrisma").onclick = function()
{

	var emailNick = document.getElementById("nickUserEmail");
	var password = document.getElementById("senhaUser");


	if(emailNick.value  != "" && password.value != "")
	{
		formdata.append("nickOuEmail", emailNick.value);
		formdata.append("minhaSenha", password.value);
		xmlhttp = new XMLHttpRequest();
		xmlhttp.onreadystatechange = function()
		{
			if(xmlhttp.readyState === 4 && xmlhttp.status === 200)
				console.log("deu algum erro");
				document.getElementById("alert_danger").style.display = "block";
				document.getElementById("alertar_hp").innerHTML = "<i class='fas fa-exclamation-circle'></i> " + xmlhttp.responseText;
		}


				xmlhttp.open("POST", "ajax/login.php", true);
				xmlhttp.send(formdata);
	}
	else
	{
		document.getElementById("alert_danger").style.display = "block";
		document.getElementById("alertar_hp").innerHTML = "<i class='fas fa-exclamation-circle'></i> Todos os parâmetros são obrigatórios!!";

	}
};