
formData = new FormData();

document.getElementById("cadastrarPrisma").onclick = function()
{
	var nick = document.getElementById("forNick");
	var email = document.getElementById("forEmail");
	var senha = document.getElementById("forPass");
	var confirmSenha = document.getElementById("forSenhaConfirma");
	var codigoRecaptcha = document.getElementById("forCodigo");

	if(nick.value != "" && email.value != "" && senha.value != "" && confirmSenha.value != "" && codigoRecaptcha.value != "")
	{

		formData.append("Apelido", nick.value);
		formData.append("email", email.value);
		formData.append("senha", senha.value);
		formData.append("confirmaSenha", confirmSenha.value);
		formData.append("recaptcha", codigoRecaptcha.value);

			http = new XMLHttpRequest();
			http.onreadystatechange = function()
			{
				if(http.readyState === 4 && http.status === 200)
					document.getElementById("displayCinco").style.display = "block";
					document.getElementById("displayCinco").innerHTML = "<i class='fas fa-exclamation-triangle'></i> " + http.responseText;
			}
			http.open("POST", "ajax/registro.php", true);
			http.send(formData);

	}
	else if(senha.value != confirmSenha.value)
	{
	
		document.getElementById("displayCinco").style.display = "block";
		document.getElementById("displayCinco").innerHTML = "<i class='fas fa-exclamation-triangle'></i> As senhas não se concidem!";
	}else

	{

	
		document.getElementById("displayCinco").style.display = "block";
		document.getElementById("displayCinco").innerHTML = "<i class='fas fa-exclamation-triangle'></i> Todos os campos são obrigatórios!";
	}
};
	

