 

 // eventos de abrir o "Esqueceu senha"

 const alvo = document.querySelectorAll('[data-animar]');
 const animacaoClass = 'animate';



 function requestOpen(){

 	document.getElementById("DisplayCamp").style.display = "none";
 	document.getElementById("DisplayRegister").style.display = "none";
 	document.getElementById("DisplayRequest").style.display = "block";
 	 document.getElementById("displayEmail").style.display = "block";
 	document.getElementById("displayNick").style.display = "block";
 		document.getElementById("displaySenha").style.display = "block";

 		 document.getElementById("displayOptionUm").style.display = "none";
 		 document.getElementById("displayOptionDois").style.display = "none";
 		  document.getElementById("displayOptionTres").style.display = "none";

 }

 function openOption(num){

 		switch(num){
 				case 1 : document.getElementById("displayOptionUm").style.display = "block";
 						 document.getElementById("displayEmail").style.display = "none";
 				 		document.getElementById("displayNick").style.display = "none";
 						document.getElementById("displaySenha").style.display = "none";
 				break;
 				case 2 :
 				 		document.getElementById("displayOptionDois").style.display = "block";
 						 document.getElementById("displayEmail").style.display = "none";
 				 		document.getElementById("displayNick").style.display = "none";
 						document.getElementById("displaySenha").style.display = "none";
 				break;
 				case 3 : 
 						document.getElementById("displayOptionTres").style.display = "block";
 						document.getElementById("displayEmail").style.display = "none";
 				 		document.getElementById("displayNick").style.display = "none";
 						document.getElementById("displaySenha").style.display = "none";
 				break;
 				case 4 : 
	 				 	document.getElementById("DisplayCamp").style.display = "block";
 						document.getElementById("DisplayRegister").style.display = "block";
 						document.getElementById("DisplayRequest").style.display = "none";
 					   document.getElementById("displayOptionUm").style.display = "none";
 		  				document.getElementById("displayOptionTres").style.display = "none";
 						document.getElementById("displayOptionDois").style.display = "none";
 						 document.getElementById("displayEmail").style.display = "none";
 				 		document.getElementById("displayNick").style.display = "none";
 						document.getElementById("displaySenha").style.display = "none";
 				break;
 		}
 }


 function animeScroll(){
 	//const windowTop = pageYOffset + (window.innerHeight * 2) /3;
 	alvo.forEach(function(element){
 			element.classList.add(animacaoClass);
 		
 	});

function eventoPassar(){
	alvo.classList.add(animacaoClass);
}

 }
 window.addEventListener('scroll', function(){
 	animeScroll();
 })

